import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';
import InputGroup from 'react-bootstrap/InputGroup';
import axios from 'axios';
import React, { useEffect, useState } from "react";
import Toast from 'react-hot-toast';
function Tabla({ tabla, filas_por_pagina }) {
    const [columnas, setcolumnas] = useState([])
    const [filas, setfilas] = useState([])
    const [data, set_data] = useState([])
    let [render, set_render] = useState([])
    let [data_select, set_data_select] = useState([]);
    let [anterior_state, set_anterior_state] = useState(false);
    let [siguiente_state, set_siguiente_state] = useState(false);
    let [disabled_select, set_disabled_select] = useState(false);
    const [currentPageNumber, setCurrentPageNumber] = useState(1);
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    let [id_borrar, set_id_borrar] = useState(0);
    const baseUrl = 'http://localhost:4000/' + tabla;
    async function Buscar_data() {
        try {
            const response = await fetch(baseUrl);
            if (response.ok) {
                response.json().then(data => set_tabla(data))
            }
        } catch (error) {

        }
    }
    useEffect(() => {
        Buscar_data();
    });

    const goOnPrevPage = () => {
        if (currentPageNumber === 1) {
            return;
        }
        setCurrentPageNumber((prev) => prev - 1);
    };
    const goOnNextPage = () => {
        if (currentPageNumber === data.length / filas_por_pagina) {
            return;
        }
        setCurrentPageNumber((prev) => prev + 1);
    };

    const handleSelectChange = (e) => {
        setCurrentPageNumber(e.target.value);
    };

    function set_tabla(data) {
        try {
            setcolumnas(Object.keys(data[0]));
            set_data(data);

            if (data.length > filas_por_pagina) {
                const start = (currentPageNumber - 1) * filas_por_pagina;
                const end = currentPageNumber * filas_por_pagina;
                if (start >= end) {
                    set_render(data);
                    set_disabled_select(true);
                    set_anterior_state(true);
                    set_siguiente_state(true);
                    set_data_select([]);
                } else {
                    set_disabled_select(false);
                    set_anterior_state(false);
                    set_siguiente_state(false);
                    set_data_select(Array(data.length / filas_por_pagina));
                    set_render(data.slice(start, end));
                }
            } else {
                set_anterior_state(true);
                set_disabled_select(true);
                set_siguiente_state(true);
                set_data_select(Array(data.length / data.length));
                set_render(data);
            }
            setfilas(render);
        } catch (error) {

        }
    }

    function editar(event, id) {
        try {
            event.stopPropagation();
            document.getElementById('enviar_datos').reset();
        } catch (error) {

        }
    }

    function borrar(event, id) {
        try {
            event.stopPropagation();
            set_id_borrar(id);
            handleShow();
        } catch (error) {
        }
    }

    function agregar(event) {
        try {
            event.stopPropagation();
            let form = document.getElementById('cont_enviar_datos');
            let agregar_boton = document.getElementById('agregar_boton');
            if(form.classList.contains('d-none')){
                agregar_boton.innerHTML = 'Sair del formulario';
            } else {
                agregar_boton.innerHTML = 'Agregar';
            }
            form.classList.toggle('d-none');
            document.getElementById('enviar_datos').reset();
         document.getElementById(tabla).classList.toggle('d-none');
        } catch (error) {

        }
    }

    function borrar_call(event) {
        try {
            event.stopPropagation();
            axios.delete('http://localhost:4000/' + tabla + '/' + id_borrar);
            handleClose();
            Toast.success(
                'Dato borrado',
                {
                    duration: 2000,
                }
            );
        } catch (error) {

        }
    }

    return (
        <Container>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Vas a borrar la fila</Modal.Title>
                </Modal.Header>
                <Modal.Body> ¿ Estas seguro que vas a borrar el dato?</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Cerrar
                    </Button>
                    <Button variant="danger" onClick={(event) => { borrar_call(event); }}>
                        Borrar fila
                    </Button>
                </Modal.Footer>
            </Modal>

            <Card.Header>
                <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{tabla}</h2>
                <Container>
                    <Button id='agregar_boton' onClick={(event) => agregar(event)} variant="outline-primary mb-5">Agregar</Button>
                    <InputGroup id='paginado_input_group' className="mb-3">
                        <InputGroup.Text id="paginado_text_input_group">Paginado</InputGroup.Text>
                        <Form.Select disabled={disabled_select} id='paginado' className='margin_left_085rem pb-3' name="paginado" onChange={handleSelectChange} value={currentPageNumber} >
                            {Array.from(data_select)
                                .map((e, i) => i + 1)
                                .map((val) => {
                                    return <option key={val}>{val}</option>;
                                })}
                        </Form.Select>
                    </InputGroup>
                </Container>
            </Card.Header>
            <Table id={tabla} key={tabla} variant="light" bordered hover className='table' responsive>
                <thead>
                    <tr>
                        {columnas.map(heading => {
                            return <th className='mayus' key={heading}>{heading}</th>
                        })}
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {filas.map((item) => (
                        <tr key={item.id}>
                            {Object.values(item).map((val) => (
                                <td className='mayus'>{val}</td>
                            ))}
                            <td >
                                <Button onClick={(event) => editar(event, item.id)} variant="outline-primary">Editar</Button>
                                <Button onClick={(event) => borrar(event, item.id)} variant="outline-danger margin_left_085rem">Borrar</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <Container className='float-right' id="paginado_container">
                <Button id='anterior_tabla' disabled={anterior_state} variant="outline-primary" onClick={goOnPrevPage}>Anterior</Button>
                <Button id='siguiente_tabla' disabled={siguiente_state} variant="outline-primary margin_left_085rem" onClick={goOnNextPage}>Siguiente</Button>
            </Container>

        </Container>
    );
}
export default Tabla;
