import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React, { useState } from 'react'
function Login() {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [emailError, setEmailError] = useState('')
    const [passwordError, setPasswordError] = useState('')
    const onButtonClick = () => {
        try {
            // Set initial error values to empty
            setEmailError('')
            setPasswordError('')

            // Check if the user has entered both fields correctly
            if ('' === email) {
                setEmailError('Porfavor, escribi tu email')
                return
            }

            if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
                setEmailError('Porfavor escribi un email valido')
                return
            }

            if ('' === password) {
                setPasswordError('Porfavor escribi una contraseña')
                return
            }

            if (password.length < 4) {
                setPasswordError('La contraseña tiene que tener mas de 4 caracteres')
                return
            }

        } catch (error) {

        }
    }
    function resultado(json) {
        try {
            console.log(json);
        } catch (error) {

        }
    }
    function handleSubmit(event) {
        try {
            event.preventDefault()
            fetch('http://localhost:8000/users', {
                method: 'GET',
                headers: { 'Content-Type': 'application/json' }
            }).then(res => resultado(res.json())).then(
                res => resultado(res.json())
            )
        } catch (error) {

        }
    }
    const getData=()=>{
        fetch('data.json'
        ,{
          headers : { 
            'Content-Type': 'application/json',
            'Accept': 'application/json'
           }
        }
        )
          .then(function(response){
            console.log(response)
            return response.json();
          })
          .then(function(myJson) {
            console.log(myJson);
          });
      }
      useEffect(()=>{
        getData()
      },[])
    return (
        <Form className='login-form' onSubmit={event => handleSubmit(event)}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Direccion de E-mail</Form.Label>
                <Form.Control type="email" value={email} placeholder="Ingresa tu e-mail" onChange={(ev) => setEmail(ev.target.value)} />
                <Form.Text className="text-muted">
                    Nunca vamos a compartir tu e-mail con nadie
                </Form.Text>

            </Form.Group>

            <label className="errorLabel">{emailError}</label>

            <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Contraseña</Form.Label>
                <Form.Control value={password} type="password" placeholder="Contraseña" onChange={(ev) => setPassword(ev.target.value)} />

            </Form.Group>
            <label className="errorLabel">{passwordError}</label>
            <Button onClick={onButtonClick} variant="primary" type="submit">
                Enviar
            </Button>
        </Form>
    );

}

export default Login;
