import Tabla from "../components/Tabla";
import custom_Form from "../components/Custom_Form";
import React, { useEffect, useState } from "react";

function Libros() {
  const tabla = 'libros';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  const [datos, setDatos] = useState([]);
  function error() {
    setTimeout(() => {
      document.cookie = 'error=true';
      window.location = 'http://localhost:3000/Login';
    }, 2500);

  }
  function pasar_data(data) {
    try {
      set_data(data);
      set_filas(Object.keys(data[0]));
    } catch (error) {

    }
  }
  const fetchData = async()=> {
    const response = await fetch(baseUrl);
    if (response.ok) {
      response.json().then(data => pasar_data(data))
    }

    return response
  }
  useEffect(() => {

    fetchData()
  }, data);

  function enviar(event) {
    try {

    } catch (error) {

    }
  }
  var privilegio = localStorage.getItem('privilegio');
  if (privilegio != null && privilegio.length > 0) {
    var clase = '';
    if (Number.parseInt(privilegio) > 1) {
      clase = 'd-none';
    }
    return [
      <div key={'libro_contenedor_div'}>
        <Tabla acciones={clase} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        {custom_Form(filas, 'enviar_datos', 'Cargar', 'd-none', enviar, datos, setDatos)}
      </div>
    ];
  } else {
    error();
  }

}
export default Libros;
