import Table from 'react-bootstrap/Table';
function Tabla({ tabla }) {

    function getHeadings(data) {
        return Object.keys(data).map(key => {
            return <th>{key}</th>;
        });
    }

    function getRows(data) {
        return data.map(obj => {
            return <tr>{getCells(obj)}</tr>;
        });
    }

    function getCells(obj) {
        return Object.values(obj).map(value => {
            return <td>{value}</td>;
        });
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(e => render(e))
            } else {
                console.log('No render');
            }
        } catch (error) {
            console.log(error);
        }
    }
    Buscar_data();

    function render(e) {    
        <Table responsive="sm" striped bordered hover>
            <h2 className="ml-3 text-center" style={{ color: "green" }}> Usuarios </h2>
            <thead>{getHeadings(e)}</thead>
            <tbody>{getRows(e)}</tbody>
        </Table>
    }
}
export default Tabla;
