import Table from 'react-bootstrap/Table';
import React, { useState } from "react";
function Tabla({ tabla }) {
    const [Data, setData] = useState([])
    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(data => setData(data))
            } else {
                console.log('No render');
            }
        } catch (error) {
            console.log(error);
        }
    }
    Buscar_data();


    return (
        
        <Table responsive>
              <h2 className="ml-3 text-center" style={{ color: "green" }}>Inicio</h2>
            <thead>
                <tr>
                    {Object.keys(Data).map((item) => {
                        return <th key={item}>{item}.</th>;
                    })}
                </tr>
            </thead>
            <tbody>
                {[
                    ...Array(
                        Math.max(...Object.values(Data).map((item) => item.length))
                    ),
                ].map((itm, idx) => {
                    return (
                        <tr>
                            {Object.values(Data).map((item) => {
                                return (
                                    <td>
                                        {typeof item[idx] === "boolean"
                                            ? new Boolean(item[idx]).toString()
                                            : ""}
                                    </td>
                                );
                            })}
                        </tr>
                    );
                })}
            </tbody>
        </Table>
    );
}
export default Tabla;
