import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import React, { useEffect, useState } from "react";
function Tabla({ tabla, filas_por_pagina }) {
    const [columnas, setcolumnas] = useState([])
    const [filas, setfilas] = useState([])
    const [currentPageNumber, setCurrentPageNumber] = useState(1);

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(data => set_tabla(data))
            }
        } catch (error) {

        }
    }
    useEffect(() => {
        Buscar_data();
    });

    const goOnPrevPage = () => {
        if (currentPageNumber === 1) return;
        setCurrentPageNumber((prev) => prev - 1);
    };
    const goOnNextPage = () => {
        if (currentPageNumber === filas.length / filas_por_pagina) return;
        setCurrentPageNumber((prev) => prev + 1);
        console.log(currentPageNumber);
    };

    const handleSelectChange = (e) => {
        setCurrentPageNumber(e.target.value);
    };

    function set_tabla(data) {
        try {
            setcolumnas(Object.keys(data[0]));
            setfilas(data);
        } catch (error) {

        }
    }

    function editar(id) {
        try {

        } catch (error) {

        }
    }

    function borrar(id) {
        try {
            console.log(id);
        } catch (error) {

        }
    }

    function agregar() {
        try {

        } catch (error) {

        }
    }

    if (filas.length === 0) return <div>Cargando...</div>;
    return (
        <Container>

            <Card.Header>
                <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{tabla}</h2>
                <div>
                    <Button onClick={agregar()} variant="outline-primary mb-5">Agregar</Button>
                </div>
            </Card.Header>
            <Table key={tabla} variant="light" bordered hover className='table' responsive>
                <thead>
                    <tr>
                        {columnas.map(heading => {
                            return <th className='mayus' key={heading}>{heading}</th>
                        })}
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {filas.map((item) => (
                        <tr key={item.id}>
                            {Object.values(item).map((val) => (
                                <td className='mayus'>{val}</td>
                            ))}
                            <td >
                                <Button variant="outline-primary">Editar</Button>
                                <Button variant="outline-danger margin_left_085rem">Borrar</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <Container id="btn-container">
                <Button variant="outline-primary" onClick={goOnPrevPage}>Anterior</Button>
                <Button variant="outline-primary margin_left_085rem" onClick={goOnNextPage}>Siguiente</Button>
                <select className='margin_left_085rem'
                    name="page-number"
                    onChange={handleSelectChange}
                    value={currentPageNumber}
                >
                    {Array.from(Array(filas.length / filas_por_pagina))
                        .map((e, i) => i + 1)
                        .map((val) => {
                            return <option key={val}>{val}</option>;
                        })}
                </select>
            </Container>

        </Container>
    );
}
export default Tabla;
