import NavDropdown from 'react-bootstrap/NavDropdown';
import React, { useEffect, useState } from "react";
import { Nav,NavLink } from "react-bootstrap";
import { Link } from "react-router-dom";
function Menu_usuario() {
    const [data, set_data] = useState([]);
    let permiso = localStorage.getItem('privilegio');

    function get_nombre() {
        let nombre = (localStorage.getItem('nombre'));
        return nombre;
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/permisos/' + permiso);
            if (response.ok) {
                response.json().then(data => set_data(data))
            }
        } catch (error) {

        }
    }

    useEffect(() => {
        Buscar_data();
    },[]);

    if (get_nombre() == null) {
        return (
            <NavLink className="text-white" as={Link} to="/Login">
                Iniciar session
            </NavLink>
        );
    } else {
        if (get_nombre().length < 1) {

            return (
                <NavLink className="text-white" as={Link} to="/Login">
                    Iniciar session
                </NavLink>
            );

        } else if (Number.parseInt(permiso) > 0 && data.length > 0 ) {
            return (
                <NavDropdown title="Menu del usuario" id="basic-nav-dropdown">
                    <p className="text-center mayus"> {get_nombre()}</p>
                    <NavDropdown.Divider />
              
                    <NavDropdown.Divider />
                    <NavLink as={Link} to="/Salir">
                        Salir
                    </NavLink>
                </NavDropdown>
            )
        }
    }

}
export default Menu_usuario;
