import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import React, { useEffect, useState } from "react";
function Tabla({ tabla }) {
    const [columna, setcolumnas] = useState([])
    const [filas, setfilas] = useState([])
    function call(){
        fetch('http://localhost:4000/' + tabla).then(res => res.json()).then(data => {
            setcolumnas(Object.keys(data[0]));
            setfilas(data);
            console.log(columna);
        })
    }
    useEffect(() => {
      call();
      if(columna.length == 0){
        call();
      }
    }, []);

    return (
        <Container>
            <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{tabla}</h2>
            <Table className='table' responsive>
                <thead>
                    <tr>

                    </tr>
                </thead>
                <tbody>

                </tbody>
            </Table>
        </Container>
    );
}
export default Tabla;
