import Modal from 'react-bootstrap/Modal';
import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
function Modal_custom(props) {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    function click_modal(event) {
        event.stopPropagation();
        props.call();
    }

    function close_modal_func() {
        handleClose();
    }

    function show_modal_func(){
        handleShow();
    }

    return [
        <Modal key={'modal_custom_key'} show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Vas a borrar la fila</Modal.Title>
            </Modal.Header>
            <Modal.Body> Â¿ Estas seguro que vas a borrar el dato?</Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Cerrar
                </Button>
                <Button variant="danger" onClick={(event) => { click_modal(event) }}>
                    Borrar fila
                </Button>
            </Modal.Footer>
        </Modal>

    ];
}

export const close_modal = close_modal_func();
export const show_modal = show_modal_func();
export default Modal_custom;
