import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

function Custom_Form(elementos, id, titulo, clase, validar, datos, setDatos) {

    let tipos = [];
    tipos['Contraseña'] = 'password';
    tipos['email'] = 'email';
    const [entries, setEntries] = useState([]);

    function reset(event) {
        try {
            if (event.isTrusted === true) {
                event.stopPropagation();
                document.getElementById(id).reset();
                setDatos([]);
            }
        } catch (error) {

        }
    }

    function onchange_valor(event) {
        try {
            if (event.isTrusted === true) {
                setDatos({
                    ...datos,
                    [event.target.id]: event.target.value
                })
            }
        } catch (error) {

        }
    }
    function submit(event) {
        try {
            if (event.isTrusted === true) {
                event.preventDefault();
                event.stopPropagation();
                validar(event);
                const formData = new FormData(event.target);

                const newEntries = [];
                for (const [name, value] of formData) {
                  newEntries.push({
                    name,
                    value
                  });
                }
            
                setEntries(newEntries);
            }
        } catch (error) {

        }
    }

    useEffect(() => {
        console.log(entries);
      }, [entries]);
      
    return [
        <Container className={clase + ' card mb-3 '} id={"cont_" + id}>
            <div className='card-header'>
                <h3 className="ml-3 text-center mayus text-green" id={'titulo_' + id} >{titulo}</h3>

            </div>
            <Form id={id} method='POST' onSubmit={submit}>
                {elementos.map((elemento, index) => {
                    let tipo = tipos[elemento];
                    let input_group_class = 'mb-3';
                    if (elemento === 'id') {
                        tipo = 'hidden';
                        input_group_class = 'mb-3 d-none';
                    }
                    return <InputGroup className={input_group_class}>
                        <InputGroup.Text className='mayus' id={'text_' + elemento}>{elemento}</InputGroup.Text>
                        <Form.Control key={elemento + '_' + index} onChange={(ev) => onchange_valor(ev)} type={tipo} name={elemento} id={elemento}
                            placeholder={"Escribi el " + elemento}
                            aria-label={elemento}
                            aria-describedby={'text_' + elemento}
                        />
                    </InputGroup>
                })}
                <div className='card-footer mt-3'>
                    <Button onClick={event => submit(event)} variant="primary" type="submit">
                        Enviar
                    </Button>
                    <Button className='margin_left_085rem' onClick={event => reset(event)} variant="danger" type="reset">
                        Limpiar
                    </Button>
                </div>
            </Form>
        </Container>
    ];
}


export default Custom_Form;
