import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Container from 'react-bootstrap/Container';
function custom_Form(submit,elementos,id,titulo) {
   let keys = (Object.keys(elementos[0]));
   console.log(keys);
    return [
        <Container>
            <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{titulo}</h2>
        <Form className='' id={id} method='POST' onSubmit={submit}>
            {keys.map(elemento => {
                return <InputGroup className="mb-3">
                    <InputGroup.Text className='mayus' id="basic-addon1">{elemento.nombre}</InputGroup.Text>
                    <Form.Control name={elemento.name} id={elemento.name}
                        placeholder={elemento.placeholder}
                        aria-label={elemento.nombre}
                        aria-describedby="basic-addon1"
                    />
                </InputGroup>
            })}
        </Form>
        </Container>
    ];
}
export default custom_Form;
