import React,{ useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import Container from 'react-bootstrap/Container';
function Home() {

  const [index, setIndex] = useState(0);
  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };
  setTimeout(() => {
    document.getElementById('titulo_app').innerHTML = 'Inicio';
  }, 2500);
  return (
    <Container>

    <Carousel activeIndex={index} onSelect={handleSelect}>
      <Carousel.Item className='w-75 h-75'>
        <img
          className="d-block w-100"
          src="../imagenes/juego_tronos.jpg"
          alt="Portadas de los libros de Juego de tronos"
        />
        <Carousel.Caption>
          <h5>Juego de tronos</h5>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://phantom-expansion.unidadeditorial.es/53607cd150793488d1b4f1be32ed7bed/f/webp/assets/multimedia/imagenes/2024/05/15/17157596218558.jpg"
          alt="Second slide"
        />
        <Carousel.Caption>
          <h5>Second slide label</h5>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="https://phantom-expansion.unidadeditorial.es/53607cd150793488d1b4f1be32ed7bed/f/webp/assets/multimedia/imagenes/2024/05/15/17157596218558.jpg"
          alt="Third slide"
        />
        <Carousel.Caption>
          <h5>Third slide label</h5>
          <p>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
    </Container>
  );
}

export default Home;
