import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import React, { useEffect, useState } from "react";
function Tabla({ tabla }) {
    const [columnas, setcolumnas] = useState([])
    const [filas, setfilas] = useState([])
    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(data => set_tabla(data))
            } else {
                console.log('No render');
            }
        } catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        Buscar_data();
    }, []);


    function set_tabla(data) {
        try {
            setcolumnas(Object.keys(data[0]));
            setfilas(data);
        } catch (error) {

        }
    }

    return (
        <Container>
            <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{tabla}</h2>
            <Table variant="light" bordered hover className='table' responsive>
                <thead>
                    <tr>
                        {columnas.map(heading => {
                            return <th className='mayus' key={heading}>{heading}</th>
                        })}
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {filas.map((item) => (
                        <tr key={item.id}>
                            {Object.values(item).map((val) => (
                                <td className='mayus'>{val}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </Table>
        </Container>
    );
}
export default Tabla;
