import Tabla from "../components/Tabla";
import custom_Form from "../components/Custom_Form";
import React, { useEffect, useState, useCallback } from "react";

function Usuarios(props) {
  const tabla = 'usuarios';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  const [datos, setDatos] = useState([]);

  function enviar(event,campos) {
    try {

    } catch (error) {
      props.error_mensaje('Ocurrio un error');
    }
  }

  function pasar_data(data) {
    try {
      set_data(data);
      set_filas(Object.keys(data[0]));
    } catch (error) {
      props.error_mensaje('Ocurrio un error');
    }
  }
  const fetchData = useCallback(async () => {
    const response = await fetch(baseUrl);
    if (response.ok) {
      response.json().then(data => pasar_data(data))
    }
  }, [])

  useEffect(() => {
    fetchData()
  }, [fetchData]);

  useEffect(() => {
    const interval = setInterval(() => {
      fetchData() 
    }, 100);
    return () => clearInterval(interval);
  },[fetchData]);

  if (localStorage.getItem('privilegio') === '1') {
    return [
      <div key={'contenedor_usuarios_div'}>
        <Tabla error_mensaje={props.error_mensaje} acciones={''} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        {custom_Form(filas, 'enviar_datos', 'Cargar', 'd-none', enviar, datos, setDatos)}
      </div>
    ];
  } else {
    props.redirect();
  }
}
export default Usuarios;
