import Table from 'react-bootstrap/Table';
import React, { useState } from "react";
function Tabla({ tabla }) {
    const [Data, setData] = useState([])
    let headers = [];
    let rows = [];
    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(data => set_tabla(data))
            } else {
                console.log('No render');
            }
        } catch (error) {
            console.log(error);
        }
    }
    Buscar_data();

    function set_tabla(data) {
        setData(data);
        headers = Object.keys(Data[0]);
        rows = Data.map(item => Object.values(item));
    }

    return (

        <Table responsive>
            <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{tabla}</h2>

        </Table>
    );
}
export default Tabla;
