import Tabla from "../components/Tabla";
import custom_Form from "../components/Custom_Form";
import React, { useEffect, useState } from "react";

function Libros() {
  const tabla = 'libros';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  function error() {
    setTimeout(() => {
      document.cookie = 'error=true';
      window.location = 'http://localhost:3000/Login';
    }, 2500);

  }

  async function Buscar_data() {
    try {
      const response = await fetch(baseUrl);
      if (response.ok) {
        response.json().then(data => pasar_data(data))
      }
    } catch (error) {

    }
  }
  function pasar_data(data) {
    try {
      set_data(data);
      set_filas(Object.keys(data[0]));
    } catch (error) {

    }
  }
  useEffect(() => {
    Buscar_data();
  });


  if (localStorage.getItem('privilegio') != null && localStorage.getItem('privilegio').length > 0) {
    return [
      <div>
        <Tabla filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        {custom_Form(filas, 'enviar_datos', 'Cargar','d-none')}
      </div>
    ];
  } else {
    error();
  }

}
export default Libros;
