import React, { useState } from 'react';
import Toast from 'react-bootstrap/Toast';
import ToastContainer from 'react-bootstrap/ToastContainer';
function Mensaje(titulo, mensaje, css) {
  const [showA, setShowA] = useState(true);
  const toggleShowA = () => setShowA(!showA);

  return (
    <div
      aria-live="polite"
      aria-atomic="true"
      className="bg-dark position-relative top_index"
    >
      <ToastContainer
        className="p-3"
        position={'top-end'}
      >
        <Toast autohide show={showA} onClose={toggleShowA} className={css}>
          <Toast.Header className={css}>
            <strong className="me-auto">{titulo}</strong>
          </Toast.Header>
          <Toast.Body>{mensaje}</Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
}

export const LLmar = ({ titulo, mensaje, css }) => (
  <div>
    {Mensaje(titulo, mensaje, css)}
  </div>
)

export default Mensaje;
