import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import React, { useEffect, useState } from "react";
function Tabla({ tabla, filas_por_pagina }) {
    const [columnas, setcolumnas] = useState([])
    const [filas, setfilas] = useState([])
    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(data => set_tabla(data))
            }
        } catch (error) {

        }
    }
    useEffect(() => {
        Buscar_data();
    }, []);


    function set_tabla(data) {
        try {
            setcolumnas(Object.keys(data[0]));
            setfilas(data);
        } catch (error) {

        }
    }

    function editar(id) {
        try {

        } catch (error) {

        }
    }

    function borrar(id) {
        try {
            console.log(id);
        } catch (error) {

        }
    }

    function agregar() {
        try {

        } catch (error) {

        }
    }

    const calculateRange = (data, rowsPerPage) => {
        const range = [];
        const num = Math.ceil(data.length / rowsPerPage);
        let i = 1;
        for (let i = 1; i <= num; i++) {
          range.push(i);
        }
        return range;
      };

      const sliceData = (data, page, rowsPerPage) => {
        return data.slice((page - 1) * rowsPerPage, page * rowsPerPage);
      };

    return (
        <Container>
            <Card.Header>
                <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{tabla}</h2>
                <Button onClick={agregar()} variant="outline-primary mb-5">Agregar</Button>
            </Card.Header>
            <Table variant="light" bordered hover className='table' responsive>
                <thead>
                    <tr>
                        {columnas.map(heading => {
                            return <th className='mayus' key={heading}>{heading}</th>
                        })}
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {filas.map((item) => (
                        <tr key={item.id}>
                            {Object.values(item).map((val) => (
                                <td className='mayus'>{val}</td>
                            ))}
                            <td >
                                <Button variant="outline-primary">Editar</Button>
                                <Button variant="outline-danger margin_left_085rem">Borrar</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </Container>
    );
}
export default Tabla;
