    } catch (error) {
      props.error_mensaje('Ocurrio un error');
    }
  }

  var privilegio = localStorage.getItem('privilegio');
  if (privilegio != null && privilegio.length > 0) {
    var clase = '';
    if (privilegio !== "Cd17") {
      clase = 'd-none';
    }
    return [
      <div key='libro_contenedor_div'>
        <Tabla setkey={setkey} remove_class_existe={props.remove_class_existe} success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} key='tabla' fetchData={fetchData} acciones={clase} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        <CustomForm error_mensaje={props.error_mensaje} elementos={filas} id={'enviar_datos'} titulo={'Cargar'} clase={'d-none'} datos={datos} setDatos={setDatos} validar={enviar} ></CustomForm>
      </div>
    ];
  } else {
    props.redirect();
  }

}
export default Libros;
