import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
function custom_Form(elementos, id, titulo) {
    let keys = (Object.keys(elementos));

    function reset(event){
        event.stopPropagation();
    }
    function submit(event){
        try {
            event.preventDefault();
            event.stopPropagation();
            
        } catch (error) {
            
        }
    }
    return [
        <Container className='card mt-3' id={"cont_" +  id}>
           <div className='card-header'>
           <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{titulo}</h2>
           </div>
            <Form id={id} method='POST' onSubmit={submit}>
                {keys.map(elemento => {
                    return <InputGroup className="mb-3">
                        <InputGroup.Text className='mayus' id={'text_' +  elemento}>{elemento}</InputGroup.Text>
                        <Form.Control name={elemento} id={elemento}
                            placeholder={"Escribi el " + elemento}
                            aria-label={elemento}
                            aria-describedby={'text_' + elemento}
                        />
                    </InputGroup>
                })}
              <div className='card-footer mt-3 bg-light'>
              <Button onClick={event => submit(event)} variant="primary" type="submit">
                    Enviar
                </Button>
                <Button className='margin_left_085rem' onClick={event => reset(event)} variant="danger" type="reset">
                    Limpiar
                </Button>
              </div>
            </Form>
        </Container>
    ];
}
export default custom_Form;
