import { useState } from 'react';
import Toast from 'react-hot-toast';
import custom_Form from "../components/Custom_Form";

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    let data = [{
        "email": "@gmail.com",
        "Contraseña": ""
    }];
    let columnas = Object.keys(data[0]);
    function getCookie(key) {
        var b = document.cookie.match("(^|;)\\s*" + key + "\\s*=\\s*([^;]+)");
        return b ? b.pop() : "";
    }



    function reset_form(event) {
        try {
            event.stopPropagation();
            setEmail('');
            setPassword('');
        } catch (error) {
            error_mensaje('Ocurrio un error');
        }
    }

    function error_mensaje(titulo_mensaje) {
        Toast.error(
            titulo_mensaje,
            {
                duration: 2000,
            }
        );
    }

    const onButtonClick = (event) => {
        try {
            event.preventDefault();
            
            if ('' === email) {
                error_mensaje('Porfavor, escribi tu email')
                return
            }

            if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
                error_mensaje('Porfavor escribi un email valido')
                return
            }

            if ('' === password) {
                error_mensaje('Porfavor escribi una contraseña')
                return
            }

            if (password.length < 4) {
                error_mensaje('La contraseña tiene que tener mas de 4 caracteres')
                return
            }
            Buscar_data();

        } catch (error) {
            error_mensaje('Ocurrio un error');
        }
    }


    function recibir_respuesta(e) {
        try {
            let obj = e.find(o => o.email === email && o.Contraseña === password);
            if (obj !== null) {
                localStorage.setItem('nombre', obj.nombre);
                localStorage.setItem('privilegio', obj.privilegio);
                Toast.success(
                    'Bienvenido: ' + obj.nombre,
                    {
                        duration: 2000,
                    }
                );
                setTimeout(() => {
                    window.location = 'http://localhost:3000/';
                }, 2500);

            } else {
                error_mensaje('Email o contraseña incorrecto')
            }
        } catch (error) {
            error_mensaje('Email o contraseña incorrecto')
        }
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/usuarios');
            if (response.ok) {
                response.json().then(e => recibir_respuesta(e))
            } else {
                error_mensaje('Ocurrio un error');
            }
        } catch (error) {
            error_mensaje('Ocurrio un error');
        }
    }

    if (localStorage.getItem('privilegio') == null || localStorage.getItem('privilegio').length === 0) {
        if (getCookie('error') === 'true') {
            error_mensaje('No tenes permisos para acceder');
            document.cookie = "error=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
        }
        return [
            custom_Form(columnas, 'login', 'Iniciar session',' mt-3',onButtonClick,reset_form)
        ];
    } else {
        window.location = 'http://localhost:3000/Libros';
    }
}

export default Login;
