import NavDropdown from 'react-bootstrap/NavDropdown';
import React, { useEffect, useState } from "react";
import { NavLink } from "react-bootstrap";
import { Link } from "react-router-dom";
import Modal_custom from './Modal_custom';

function Menu_usuario(props) {

    const [show, setShow] = useState(false);
    const [data, set_data] = useState([]);
    let permiso = localStorage.getItem('privilegio');

    function get_nombre() {
        let nombre = (localStorage.getItem('nombre'));
        return nombre;
    }

    async function Buscar_data() {
        try {
            if(permiso != null){
                const response = await fetch('http://localhost:4000/permisos/' + permiso);
                if (response.ok) {
                    response.json().then(data => set_data(data))
                }
            }
           
        } catch (error) {
            props.error_mensaje('Ocurrio un error');
        }
    }

    useEffect(() => {
        Buscar_data();
    },[]);

    function salir(event){
     try {
        event.preventDefault();
        event.stopPropagation();
        setShow(true);
     } catch (error) {
        
     }
    }
   

    if (get_nombre() == null) {
        return (
            <NavLink className="text-white" as={Link} to="/Login">
                Iniciar session
            </NavLink>
        );
    } else {
        if (get_nombre().length < 1) {

            return (
                <NavLink className="text-white" as={Link} to="/Login">
                    Iniciar session
                </NavLink>
            );

        } else if (Number.parseInt(permiso) > 0) {
            try {
                return (
                    <div>
                         <Modal_custom boton_texto={'Salir'} body={'¿ Estas seguro que vas a cerrar la session?'} titulo={'Vas a cerrar la session'} call={salir} show={show} setShow={setShow} ></Modal_custom>
                         <NavDropdown title="Menu del usuario" id="basic-nav-dropdown">
                        <p className="text-center mayus"> {get_nombre()}</p>
                        <NavDropdown.Divider />
                        {data['tablas'].split(',').map( (elemento,index) => {
                            return <NavLink key={index} className="mayus" as={Link} to={'/' + elemento} onClick={event => {
                                document.getElementById('basic-nav-dropdown').click();
                                props.remove_class_existe('active');
                                event.target.classList.add('active');
                            }}>
                                {elemento}
                            </NavLink>
                        })}
                        <NavDropdown.Divider />
                        <NavLink onClick={event => salir(event)}>
                            Salir
                        </NavLink>
                    </NavDropdown>
                    </div>
                 
                )

            } catch (error) {
                props.error_mensaje('Ocurrio un error');
            }
        }
    }

}
export default Menu_usuario;
