import Custom_Form from "../components/Custom_Form";
import { useState } from 'react';
import { Container } from 'react-bootstrap';
function Login(props) {

    let data = [{
        "email": "@gmail.com",
        "Contraseña": ""
    }];
    const [datos, setDatos] = useState([]);
    let columnas = Object.keys(data[0]);
    function getCookie(key) {
        var b = document.cookie.match("(^|;)\\s*" + key + "\\s*=\\s*([^;]+)");
        return b ? b.pop() : "";
    }

    const onButtonClick = (event) => {
        try {
            event.preventDefault();
            var email = datos['email'];
            var password = datos['Contraseña'];

            if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
                props.error_mensaje('Porfavor escribi un email valido')
                return
            }

            if (password.length < 4) {
                props.error_mensaje('La contraseña tiene que tener mas de 4 caracteres')
                return
            }
            Buscar_data();

        } catch (error) {
            props.error_mensaje('Ocurrio un error');
        }
    }


    function recibir_respuesta(e) {
        try {
            var email = datos['email'];
            var password = datos['Contraseña'];
            let obj = e.find(o => o.email === email && o.Contraseña === password);
            if (obj !== null) {
                localStorage.setItem('nombre', obj.nombre);
                localStorage.setItem('privilegio', obj.privilegio);
                props.succes_mensaje('Bienvenido: ' + obj.nombre);
                setTimeout(() => {
                    window.location = 'http://localhost:3000/';
                }, 2500);

            } else {
                props.error_mensaje('Email o contraseña incorrecto')
            }
        } catch (error) {
            props.error_mensaje('Email o contraseña incorrecto error')
        }
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/usuarios');
            if (response.ok) {
                response.json().then(e => recibir_respuesta(e))
            } else {
                props.error_mensaje('Ocurrio un error');
            }
        } catch (error) {
            props.error_mensaje('Ocurrio un error');
        }
    }
 
    setTimeout(() => {
        document.getElementById('titulo_app').innerHTML = 'Iniciar session';
    }, 1500);

    if (localStorage.getItem('privilegio') == null || localStorage.getItem('privilegio').length === 0) {
        if (getCookie('error') === 'true') {
            props.error_mensaje('No tenes permisos para acceder');
            document.cookie = "error=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
        }
        return [
            <Container>
                <Custom_Form error_mensaje={props.error_mensaje} elementos={columnas} id={'login'} titulo={''} clase={''} datos={datos} setDatos={setDatos} validar={onButtonClick} ></Custom_Form>
            </Container>

        ];
    } else {
        window.location = 'http://localhost:3000/Libros';
    }
}

export default Login;
