import Table from 'react-bootstrap/Table';
function Tabla({ tabla }) {

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' + tabla);
            if (response.ok) {
                response.json().then(data => render(data))
            } else {
                console.log('No render');
            }
        } catch (error) {
            console.log(error);
        }
    }
    Buscar_data();

    function render(data) {
     
    }

    const keys = Object.keys(data.length ? data[0] : {});
    <Table responsive="sm" striped bordered hover>
        <h2 className="ml-3 text-center" style={{ color: "green" }}> {tabla} </h2>
        <thead>
            <tr>
                {keys.map((item, idx) => (
                    <th key={idx}>{item}</th>
                ))}
            </tr>
        </thead>
        <tbody>
            {data.map((item, idx) => (
                <tr key={idx}>
                    {keys.map((key, idx) => (
                        <td>{item[key]}</td>
                    ))}
                </tr>
            ))}
        </tbody>
    </Table>
}
export default Tabla;
