import NavDropdown from 'react-bootstrap/NavDropdown';

function Menu_usuario() {
    const [data, set_data] = useState([]);
    let permiso = localStorage.getItem('privilegio');

    function get_nombre() {
        let nombre = (localStorage.getItem('nombre'));
        return nombre;
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/permisos/' + permiso);
            if (response.ok) {
                response.json().then(data => set_data(data))
            }
        } catch (error) {

        }
    }

    useEffect(() => {
        Buscar_data();
    });

    function get_profile() {
        try {
            if (get_nombre() == null) {
                return (
                    <Nav.Link className="text-white" as={Link} to="/Login">
                        Iniciar session
                    </Nav.Link>
                );
            } else {
                if (get_nombre().length < 1) {

                    return (
                        <Nav.Link className="text-white" as={Link} to="/Login">
                            Iniciar session
                        </Nav.Link>
                    );

                } else if (permiso === '1') {
                    return (
                        <NavDropdown title="Menu del usuario" id="basic-nav-dropdown">
                            <p className="text-center mayus"> {get_nombre()}</p>
                            <NavDropdown.Divider />
                            {data.map(elemento => {
                                return <Nav.Link className="mayus" as={Link} to={'/' + elemento} onClick={e => {
                                    document.getElementById('basic-nav-dropdown').click();
                                }}>
                                    {elemento}
                                </Nav.Link>
                            })}
                            <NavDropdown.Divider />
                            <Nav.Link as={Link} to="/Salir">
                                Salir
                            </Nav.Link>
                        </NavDropdown>
                    );
                }
            }
        } catch (error) {

        }
    }
}
export default Menu_usuario;
