import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';

function custom_Form(elementos, id, titulo) {
    let keys = [];

    function get_keys(){
        if(elementos.length > 0){
            Object.keys(elementos[0]);
        }
     
    }
    function reset(event) {
        event.stopPropagation();
        document.getElementById(id).reset();
    }
    function submit(event) {
        try {
            event.preventDefault();
            event.stopPropagation();

        } catch (error) {

        }
    }

    useEffect(() => get_keys(), [elementos])

    return [
        <Container className='card mt-3 d-none' id={"cont_" + id}>
            <div className='card-header'>
                <h2 className="ml-3 text-center mayus" id={'titulo_' + id} style={{ color: "green" }}>{titulo}</h2>
            </div>
            <Form id={id} method='POST' onSubmit={submit}>
                {keys.map(elemento => {
                    let tipo = elemento;
                    let input_group_class = 'mb-3';
                    if(elemento == 'id'){
                        tipo = 'hidden';
                        input_group_class = 'mb-3 d-none';
                    }
                    return <InputGroup className={input_group_class}>
                        <InputGroup.Text className='mayus' id={'text_' + elemento}>{elemento}</InputGroup.Text>
                        <Form.Control type={tipo} name={elemento} id={elemento}
                            placeholder={"Escribi el " + elemento}
                            aria-label={elemento}
                            aria-describedby={'text_' + elemento}
                        />
                    </InputGroup>
                })}
                <div className='card-footer mt-3'>
                    <Button onClick={event => submit(event)} variant="primary" type="submit">
                        Enviar
                    </Button>
                    <Button className='margin_left_085rem' onClick={event => reset(event)} variant="danger" type="reset">
                        Limpiar
                    </Button>
                </div>
            </Form>
        </Container>
    ];
}
export default custom_Form;
