import Tabla from "../components/Tabla";
import Custom_Form from "../components/Custom_Form";
import React, { useEffect, useState, useCallback } from "react";
import axios from 'axios';
function Permisos(props) {
  const tabla = 'permisos';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  const [datos, setDatos] = useState([]);

  const fetchData = useCallback(async () => {
    const response = await fetch(baseUrl);
    if (response.ok) {
      response.json().then(data => pasar_data(data))
    } else {
      props.error_mensaje('Ocurrio un error');
    }
  }, [])

  useEffect(() => {
    fetchData()
  }, [fetchData]);

  useEffect(() => {
    const interval = setInterval(() => {
      fetchData()
    }, 100);
    return () => clearInterval(interval);
  }, [fetchData]);

  function pasar_data(data) {
    try {
      set_data(data);
      set_filas(Object.keys(data[0]));
    } catch (error) {
      props.error_mensaje('Ocurrio un error');
    }
  }


  const send_data = useCallback(async () => {
  console.log(datos);
  }, [])

  function enviar(event, campos) {
    try {
      let data = [];
      campos.forEach(element => {
      data[element['name']] = element['value'];
      });
      setDatos(data);
      send_data();
    } catch (error) {
      props.error_mensaje('Ocurrio un error');
    }
  }

  if (localStorage.getItem('privilegio') === '1') {

    return [
      <div key={'div_contenedor_permisos'}>
        <Tabla remove_class_existe={props.remove_class_existe} success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} acciones={''} key={'tabla'} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        <Custom_Form error_mensaje={props.error_mensaje} elementos={filas} id={'enviar_datos'} titulo={'Cargar'} clase={'d-none'} datos={datos} setDatos={setDatos} validar={enviar} ></Custom_Form>
      </div>
    ];
  } else {
    props.redirect();
  }

}
export default Permisos;
