import Table from 'react-bootstrap/Table';
function Tabla({ tabla }) {

    function getHeadings(data) {
        return Object.keys(data[0]).map(key => {
            return <th>{key}</th>;
        });
    }

    function getRows(data) {
        return data.map(obj => {
            return <tr>{getCells(obj)}</tr>;
        });
    }

    function getCells(obj) {
        return Object.values(obj).map(value => {
            return <td>{value}</td>;
        });
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/' +  tabla);
            if (response.ok) {
                response.json().then(e => recibir_respuesta(e))
            } else {
                setPasswordError('Ocurrio un error');
            }
        } catch (error) {
            setPasswordError('Ocurrio un error');
        }
    }

    function recibir_respuesta(e) {
        try {
             data = e;

        } catch (error) {
     
        }
    }

    <Table responsive="sm" striped bordered hover>

    </Table>
}
export default Tabla;
