import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
function custom_Form(submit, elementos, id, titulo) {
    let keys = (Object.keys(elementos));

    function reset(event){
        event.stopPropagation();
    }
    return [
        <Container>
            <h2 className="ml-3 text-center mayus" style={{ color: "green" }}>{titulo}</h2>
            <Form className='' id={id} method='POST' onSubmit={submit}>
                {keys.map(elemento => {
                    return <InputGroup className="mb-3">
                        <InputGroup.Text className='mayus' id="basic-addon1">{elemento}</InputGroup.Text>
                        <Form.Control name={elemento} id={elemento}
                            placeholder={"Escribi el " + elemento}
                            aria-label={elemento}
                            aria-describedby="basic-addon1"
                        />
                    </InputGroup>
                })}
                <Button onClick={event => submit(event)} variant="primary" type="submit">
                    Enviar
                </Button>
                <Button className='margin_left_085rem' onClick={event => reset(event)} variant="danger" type="reset">
                    Limpiar
                </Button>
            </Form>
        </Container>
    ];
}
export default custom_Form;
