import Tabla from "../components/Tabla";
import CustomForm from "../components/Custom_Form";
import React, { useEffect, useState, useCallback } from "react";
function Permisos(props) {
  const tabla = 'permisos';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  const [datos, setDatos] = useState([]);
  const [loading, setLoading] = useState(false);
  const fetchData = useCallback(async () => {
    function pasar_data(data) {
      try {
        set_data(data);
        set_filas(Object.keys(data[0]));
      } catch (error) {
        props.error_mensaje('Ocurrio un error');
      }
    }
    try {
      setLoading(true);
      const response = await fetch(baseUrl);
      if (response.ok) {
        response.json().then(data => pasar_data(data)).catch((error) => {
          props.error_mensaje('Ocurrio un error');
        }).finally(() => {
          setLoading(false);
        });
      }
    } catch (error) {

    }
  }, [baseUrl, props])

  useEffect(() => {
    fetchData()
  }, [fetchData]);

  useEffect(() => {
    const interval = setInterval(() => {
      fetchData()
    }, 500);
    return () => clearInterval(interval);
  }, [fetchData]);

  if (localStorage.getItem('privilegio') === 'Cd17') {

    return [
      <div key={'div_contenedor_permisos'}>
        <Tabla remove_class_existe={props.remove_class_existe} success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} acciones={''} key={'tabla'} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        <CustomForm success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} elementos={filas} id={'enviar_datos'} titulo={'Cargar'} clase={'d-none'} datos={datos} setDatos={setDatos} baseUrl={baseUrl} ></CustomForm>
      </div>
    ];
  } else {
    props.redirect();
  }

}
export default Permisos;
