import Tabla from "../components/Tabla";
import CustomForm from "../components/Custom_Form";
import React, { useState } from "react";
import Getdata from "../components/getdata";

function Libros(props) {
  const tabla = 'libros';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  const [datos, setDatos] = useState([]);

  var privilegio = localStorage.getItem('privilegio');
  if (privilegio != null && privilegio.length > 0) {
    var clase = '';
    if (privilegio !== "Cd17") {
      clase = 'd-none';
    }
    return [
      <div key='libro_contenedor_div'>
        <Getdata set_data={set_data} set_filas={set_filas} baseUrl={baseUrl} error_mensaje={props.error_mensaje} ></Getdata>
        <Tabla remove_class_existe={props.remove_class_existe} success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} key='tabla' fetchData={fetchData} acciones={clase} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        <CustomForm success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} elementos={filas} id={'enviar_datos'} titulo={'Cargar'} clase={'d-none'} datos={datos} setDatos={setDatos} baseUrl={baseUrl} ></CustomForm>
      </div>
    ];
  } else {
    props.redirect();
  }

}
export default Libros;
