import { Container } from "react-bootstrap";
import React, { useEffect, useState, useCallback } from "react";
import CustomForm from "../components/Custom_Form";
function Registrarse(props) {
    const tabla = 'usuarios';
    const baseUrl = 'http://localhost:4000/' + tabla;
    const [filas, set_filas] = useState([])
    const [datos, setDatos] = useState([]);

    setTimeout(() => {
        document.getElementById('titulo_app').innerHTML = 'Registrarse';
    }, 1500);

    const fetchData = useCallback(async () => {
        function pasar_data(data) {
            try {
                set_filas(Object.keys(data[0]));
            } catch (error) {
                props.error_mensaje('Ocurrio un error');
            }
        }
        try {
            const response = await fetch(baseUrl);
            if (response.ok) {
                response.json().then(data => pasar_data(data)).catch((error) => {
                    props.error_mensaje('Ocurrio un error');
                });
            }
        } catch (error) {

        }
    }, [baseUrl, props])

    useEffect(() => {
        fetchData()
    }, [fetchData]);

    useEffect(() => {
        const interval = setInterval(() => {
            fetchData()
        }, 500);
        return () => clearInterval(interval);
    }, [fetchData]);

    returm[
        <Container>
            <CustomForm success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} elementos={filas} id={'enviar_datos'} titulo={'Cargar'} clase={''} datos={datos} setDatos={setDatos} baseUrl={baseUrl} ></CustomForm>
        </Container>
    ];
}
export default Registrarse;
