import Tabla from "../components/Tabla";
import CustomForm from "../components/Custom_Form";
import React, { useEffect, useState, useCallback } from "react";
import axios from 'axios';
function Permisos(props) {
  const tabla = 'permisos';
  const baseUrl = 'http://localhost:4000/' + tabla;
  const [data, set_data] = useState([])
  const [filas, set_filas] = useState([])
  const [datos, setDatos] = useState([]);
  const [key, setkey] = useState('');

  const fetchData = useCallback(async () => {
    function pasar_data(data) {
      try {
        set_data(data);
        set_filas(Object.keys(data[0]));
      } catch (error) {
        props.error_mensaje('Ocurrio un error');
      }
    }
    const response = await fetch(baseUrl);
    if (response.ok) {
      response.json().then(data => pasar_data(data))
    }
  }, [baseUrl, props])

  useEffect(() => {
    fetchData()
  }, [fetchData]);

  useEffect(() => {
    const interval = setInterval(() => {
      fetchData()
    }, 100);
    return () => clearInterval(interval);
  }, [fetchData]);



  const send_data = useCallback(async (dato, metodo) => {

    function dato_enviado(mensaje) {
      try {
        props.success_mensaje(mensaje);
        document.getElementById('agregar_boton').click();
      } catch (error) {
        props.error_mensaje('Ocurrio un error');
      }
    }

    var data = Object.assign(dato);
    

    switch (metodo) {
      case 'POST':

      const {result} = await axios.post(baseUrl, document.querySelector('#enviar_datos'), {
        headers: {
          'Content-Type': 'application/json'
        }
      })
        if (result.statusText === 'Created') {
          dato_enviado('Dato insertado');
        } else {
          props.error_mensaje('Ocurrio un error');
        }
        break;
      case 'PUT':
        data = Object.assign(dato);
        const update = await axios.put(baseUrl + '/' + dato['id'], data);
        if (update.statusText === 'OK') {
          dato_enviado('Dato cambiado');
        } else {
          props.error_mensaje('Ocurrio un error');
        }
        break;
      default:
        props.error_mensaje('No se encontro la clave');
        break;
    }
  }, [baseUrl, props])

  function enviar(event, campos) {
    try {
      if (event.isTrusted === true) {
        let data = [];
        campos.forEach(element => {
          data[element['name']] = element['value'];
        });
        send_data(data, key);
      } else {
        props.error_mensaje('Ocurrio un error');
      }
    } catch (error) {
      props.error_mensaje('Ocurrio un error');
    }
  }

  if (localStorage.getItem('privilegio') === 'Cd17') {

    return [
      <div key={'div_contenedor_permisos'}>
        <Tabla setkey={setkey} remove_class_existe={props.remove_class_existe} success_mensaje={props.success_mensaje} error_mensaje={props.error_mensaje} acciones={''} key={'tabla'} filas_por_pagina={10} tabla={tabla} data={data} ></Tabla>
        <CustomForm error_mensaje={props.error_mensaje} elementos={filas} id={'enviar_datos'} titulo={'Cargar'} clase={'d-none'} datos={datos} setDatos={setDatos} validar={enviar} ></CustomForm>
      </div>
    ];
  } else {
    props.redirect();
  }

}
export default Permisos;
