import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React, { useState } from 'react';
import Toast from 'react-hot-toast';
import { Container } from 'react-bootstrap';
function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
 
    function getCookie(key) {
        var b = document.cookie.match("(^|;)\\s*" + key + "\\s*=\\s*([^;]+)");
        return b ? b.pop() : "";
      }

    const onButtonClick = (event) => {
        try {
            event.preventDefault();
            setEmailError('')
            setPasswordError('')

            if ('' === email) {
                setEmailError('Porfavor, escribi tu email')
                return
            }

            if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(email)) {
                setEmailError('Porfavor escribi un email valido')
                return
            }

            if ('' === password) {
                setPasswordError('Porfavor escribi una contraseña')
                return
            }

            if (password.length < 4) {
                setPasswordError('La contraseña tiene que tener mas de 4 caracteres')
                return
            }
            Buscar_data();

        } catch (error) {
            setPasswordError('Ocurrio un error')
        }
    }
 

    function recibir_respuesta(e) {
        try {
            let obj = e.find(o => o.email === email && o.password === password);
            if (obj !== null) {
                localStorage.setItem('nombre', obj.nombre);
                localStorage.setItem('privilegio', obj.privilegio);
                Toast.success(
                    'Bienvenido: ' + obj.nombre,
                    {
                        duration: 2000,
                    }
                );
                setTimeout(() => {
                    window.location = 'http://localhost:3000/';
                }, 2500);

            } else {
                setPasswordError('Email o contraseña incorrecto')
            }
        } catch (error) {
            setPasswordError('Email o contraseña incorrecto')
        }
    }

    async function Buscar_data() {
        try {
            const response = await fetch('http://localhost:4000/usuarios');
            if (response.ok) {
                response.json().then(e => recibir_respuesta(e))
            } else {
                setPasswordError('Ocurrio un error');
            }
        } catch (error) {
            setPasswordError('Ocurrio un error');
        }
    }

    if (localStorage.getItem('privilegio') == null || localStorage.getItem('privilegio').length === 0) {
    if(getCookie('error') === 'true'){
        Toast.error(
            'No tenes permisos para acceder',
            {
                duration: 2000,
            }
        );
        document.cookie = "error=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;";
    }
        return (
            <Container>

                <Form className='login-form'>
                    <h2 className="ml-3 text-center" style={{ color: "green" }}> Iniciar session </h2>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label>Direccion de E-mail</Form.Label>
                        <Form.Control type="email" value={email} placeholder="Ingresa tu e-mail" onChange={(ev) => setEmail(ev.target.value)} />
                        <Form.Text className="text-muted">
                            Nunca vamos a compartir tu e-mail con nadie
                        </Form.Text>

                    </Form.Group>

                    <p className="errorLabel">{emailError}</p>

                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Contraseña</Form.Label>
                        <Form.Control value={password} type="password" placeholder="Contraseña" onChange={(ev) => setPassword(ev.target.value)} />

                    </Form.Group>
                    <p className="errorLabel">{passwordError}</p>
                    <Button onClick={e => onButtonClick(e)} variant="primary" type="submit">
                        Enviar
                    </Button>
                </Form>
            </Container>
        );
    } else {
        window.location = 'http://localhost:3000/Libros';
    }
}

export default Login;
